import React, { useState, useEffect } from 'react';
import { useTranslation } from 'react-i18next';
import { useQuery } from 'react-query';
import { Chart as ChartJS, CategoryScale, LinearScale, PointElement, LineElement, Title, Tooltip, Legend } from 'chart.js';
import { Line } from 'react-chartjs-2';
import apiService from '../services/apiService';
import LoadingSpinner from '../components/LoadingSpinner';
import ProjectCard from '../components/ProjectCard';
import FundingModal from '../components/FundingModal';

ChartJS.register(CategoryScale, LinearScale, PointElement, LineElement, Title, Tooltip, Legend);

const DonorDashboard = () => {
  const { t } = useTranslation();
  const [selectedProject, setSelectedProject] = useState(null);
  const [showFundingModal, setShowFundingModal] = useState(false);

  // Fetch dashboard stats / جلب إحصائيات لوحة التحكم
  const { data: stats, isLoading: statsLoading } = useQuery(
    'donorStats',
    apiService.getDashboardStats,
    { refetchInterval: 30000 }
  );

  // Fetch projects / جلب المشاريع
  const { data: projectsData, isLoading: projectsLoading } = useQuery(
    'donorProjects',
    () => apiService.getProjects({ status: 'active,approved' }),
    { refetchInterval: 60000 }
  );

  // Fetch funding history / جلب تاريخ التمويل
  const { data: fundingHistory, isLoading: fundingLoading } = useQuery(
    'fundingHistory',
    apiService.getFundingHistory,
    { refetchInterval: 60000 }
  );

  const projects = projectsData?.results || [];

  // Chart data for funding history / بيانات الرسم البياني لتاريخ التمويل
  const chartData = {
    labels: fundingHistory?.slice(-6).map(f => new Date(f.date).toLocaleDateString()) || [],
    datasets: [
      {
        label: t('fundingAmount'),
        data: fundingHistory?.slice(-6).map(f => f.amount) || [],
        borderColor: 'rgb(59, 130, 246)',
        backgroundColor: 'rgba(59, 130, 246, 0.1)',
        tension: 0.1,
      },
    ],
  };

  const chartOptions = {
    responsive: true,
    plugins: {
      legend: {
        position: 'top',
      },
      title: {
        display: true,
        text: t('fundingTrend'),
      },
    },
    scales: {
      y: {
        beginAtZero: true,
        ticks: {
          callback: function(value) {
            return '$' + value.toLocaleString();
          }
        }
      },
    },
  };

  const handleFundProject = (project) => {
    setSelectedProject(project);
    setShowFundingModal(true);
  };

  if (statsLoading || projectsLoading) {
    return <LoadingSpinner />;
  }

  return (
    <div className="min-h-screen bg-gray-50 py-8">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        {/* Header / الرأس */}
        <div className="mb-8">
          <h1 className="text-3xl font-bold text-gray-900">
            {t('donorDashboard') || 'Donor Dashboard'}
          </h1>
          <p className="mt-2 text-gray-600">
            {t('donorDashboardSubtitle') || 'Track your contributions and discover new projects'}
          </p>
        </div>

        {/* Stats Cards / بطاقات الإحصائيات */}
        <div className="grid grid-cols-1 md:grid-cols-3 gap-6 mb-8">
          <div className="bg-white overflow-hidden shadow rounded-lg">
            <div className="p-5">
              <div className="flex items-center">
                <div className="flex-shrink-0">
                  <div className="w-8 h-8 bg-primary-500 rounded-md flex items-center justify-center">
                    <svg className="w-5 h-5 text-white" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                      <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 8c-1.657 0-3 .895-3 2s1.343 2 3 2 3 .895 3 2-1.343 2-3 2m0-8c1.11 0 2.08.402 2.599 1M12 8V7m0 1v8m0 0v1m0-1c-1.11 0-2.08-.402-2.599-1" />
                    </svg>
                  </div>
                </div>
                <div className="ml-5 w-0 flex-1">
                  <dl>
                    <dt className="text-sm font-medium text-gray-500 truncate">
                      {t('totalFunds')}
                    </dt>
                    <dd className="text-lg font-medium text-gray-900">
                      ${stats?.total_funding?.toLocaleString() || 0}
                    </dd>
                  </dl>
                </div>
              </div>
            </div>
          </div>

          <div className="bg-white overflow-hidden shadow rounded-lg">
            <div className="p-5">
              <div className="flex items-center">
                <div className="flex-shrink-0">
                  <div className="w-8 h-8 bg-secondary-500 rounded-md flex items-center justify-center">
                    <svg className="w-5 h-5 text-white" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                      <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M19 11H5m14 0a2 2 0 012 2v6a2 2 0 01-2 2H5a2 2 0 01-2-2v-6a2 2 0 012-2m14 0V9a2 2 0 00-2-2M5 11V9a2 2 0 012-2m0 0V5a2 2 0 012-2h6a2 2 0 012 2v2M7 7h10" />
                    </svg>
                  </div>
                </div>
                <div className="ml-5 w-0 flex-1">
                  <dl>
                    <dt className="text-sm font-medium text-gray-500 truncate">
                      {t('activeProjects')}
                    </dt>
                    <dd className="text-lg font-medium text-gray-900">
                      {stats?.active_projects || 0}
                    </dd>
                  </dl>
                </div>
              </div>
            </div>
          </div>

          <div className="bg-white overflow-hidden shadow rounded-lg">
            <div className="p-5">
              <div className="flex items-center">
                <div className="flex-shrink-0">
                  <div className="w-8 h-8 bg-green-500 rounded-md flex items-center justify-center">
                    <svg className="w-5 h-5 text-white" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                      <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 12l2 2 4-4m6 2a9 9 0 11-18 0 9 9 0 0118 0z" />
                    </svg>
                  </div>
                </div>
                <div className="ml-5 w-0 flex-1">
                  <dl>
                    <dt className="text-sm font-medium text-gray-500 truncate">
                      {t('verifiedReports')}
                    </dt>
                    <dd className="text-lg font-medium text-gray-900">
                      {stats?.verified_reports || 0}
                    </dd>
                  </dl>
                </div>
              </div>
            </div>
          </div>
        </div>

        {/* Funding Chart / رسم بياني للتمويل */}
        {fundingHistory && fundingHistory.length > 0 && (
          <div className="bg-white shadow rounded-lg p-6 mb-8">
            <h2 className="text-lg font-medium text-gray-900 mb-4">
              {t('fundingHistory')}
            </h2>
            <div className="h-64">
              <Line data={chartData} options={chartOptions} />
            </div>
          </div>
        )}

        {/* Available Projects / المشاريع المتاحة */}
        <div className="bg-white shadow rounded-lg">
          <div className="px-6 py-4 border-b border-gray-200">
            <h2 className="text-lg font-medium text-gray-900">
              {t('browseProjects')}
            </h2>
            <p className="mt-1 text-sm text-gray-600">
              {t('browseProjectsSubtitle') || 'Discover projects that need funding'}
            </p>
          </div>

          <div className="p-6">
            {projects.length === 0 ? (
              <div className="text-center py-12">
                <svg className="mx-auto h-12 w-12 text-gray-400" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M19 11H5m14 0a2 2 0 012 2v6a2 2 0 01-2 2H5a2 2 0 01-2-2v-6a2 2 0 012-2m14 0V9a2 2 0 00-2-2M5 11V9a2 2 0 012-2m0 0V5a2 2 0 012-2h6a2 2 0 012 2v2M7 7h10" />
                </svg>
                <h3 className="mt-2 text-sm font-medium text-gray-900">
                  {t('noProjectsAvailable') || 'No projects available'}
                </h3>
                <p className="mt-1 text-sm text-gray-500">
                  {t('checkBackLater') || 'Check back later for new projects'}
                </p>
              </div>
            ) : (
              <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
                {projects.map((project) => (
                  <ProjectCard
                    key={project.id}
                    project={project}
                    onFund={() => handleFundProject(project)}
                    showFundButton={true}
                  />
                ))}
              </div>
            )}
          </div>
        </div>

        {/* Funding Modal / نافذة التمويل */}
        {showFundingModal && selectedProject && (
          <FundingModal
            project={selectedProject}
            onClose={() => {
              setShowFundingModal(false);
              setSelectedProject(null);
            }}
            onSuccess={() => {
              setShowFundingModal(false);
              setSelectedProject(null);
              // Refetch data
              window.location.reload();
            }}
          />
        )}
      </div>
    </div>
  );
};

export default DonorDashboard;
